const lewds = require('./end');
fetch = require('node-fetch');

class lewd {

    /**
     * @param {String} img endpoint to search
     * @returns {String} url of image
     */

    async nsfw(img) {
        return fetch(`${lewds.base}${img}`)
        .then(res => res.json())
        .then(json =>  {
            if(json.url == undefined) throw Error(`[LEWDSAPI]: ${img} is not an endpint`)
            return json.url
        })
    }
}

module.exports = lewd
